def analysis(request):
    model_path = '/Users/vyassriman/PycharmProjects/sentAnalysis/sentAI/VotingClassifier.pkl'
    ai_model = joblib.load(model_path)
    prediction_label = None
    if request.method == 'POST':
        form = SentimentForm(request.POST)
        if form.is_valid():
            email_subject = form.cleaned_data['email_subject']
            email_content = form.cleaned_data['email_content']
            combined_input = email_subject + ' ' + email_content
            processed_input = preprocess_text(combined_input)
            input_vector = tfidf.transform([processed_input])
            prediction = ai_model.predict(input_vector)
            prediction_label = label_encoder.inverse_transform(prediction)[0]
    else:
        form = SentimentForm()

    context = {
        'form': form,
        'prediction': prediction_label,
    }

    return render(request, 'analysis.html', context)


